var app=new Vue({el:"#app",data:{message:"Hello Vue!",lists:[{name:"test 2",description:"testing",items:[]}],active:0,editItem:null,newItem:{name:"",amount:1,price:null},limit:5},mounted:function(){console.log("mounted"),this.setup()},created:function(){console.log("created")},methods:{setup:function(){if(this.$cookies.isKey("lists")){var t=JSON.parse(this.$cookies.get("lists"));this.lists=t}},update:function(){var t=JSON.stringify(this.lists);this.$cookies.set("lists",t,2592e3)},totalCost:function(t){var e=0;return t.forEach(function(t){e+=t.price*t.amount}),Math.round(100*e)/100},totalItems:function(t){var e=0;return t.forEach(function(t){e+=1*t.amount}),e},addItem:function(t){this.lists[this.active].items.push(t),this.update(),$("#addItem").modal("hide")},removeItem:function(t){console.log(t),this.lists[this.active].items.splice(t,1)}}});